mapper.select:
SELECT R.id, H.address, H.name as house, date_format(R.updated_at,'%Y-%m-%d %H:%i:%s') as createdAt FROM reservation R JOIN house H ON R.house_id = H.id WHERE R.member_id = #{memberId}
AND H.name LIKE CONCAT('%',#{searchKeyword},'%')
AND H.address LIKE CONCAT('%',#{searchKeyword},'%')
LIMIT #{pageStartNum}, #{itemsPerPage}

SELECT COUNT(*) AS TOTAL_CNT FROM reservation R JOIN house H ON R.house_id = H.id WHERE R.member_id = #{memberId}
AND H.name LIKE CONCAT('%',#{searchKeyword},'%')
AND H.address LIKE CONCAT('%',#{searchKeyword},'%')
